mapper.select:
SELECT PK_ID ,BRST_ECD ,TNT_NO ,ORD_NO ,BZODR_NO ,SRVBL_NO ,MODUL_ECD ,SERNO ,DPST_SERV_INSDOUTSD_CD ,TRGT_SERV_CENT_INTFC_NM ,TRGT_SERV_CENT_RSPSCD ,TRGT_SERV_CENT_RSPND_INFO ,REQE_MSG_INFO ,RETN_MSG_INFO ,SYS_DLWTH_STUCD ,REPTR_TB_APLTN ,VAL_DTINF ,RCPTN_DT ,RCPTN_TM ,REMRK ,TRD_DT ,TRD_TM ,VLD_FLG ,FAL_REAS ,CRT_TM ,UPD_TM ,NODE_ID ,CPNST_TX_STUS_CD ,EXTN_FLOWT_ID FROM TRDCM_FLW WHERE ORD_NO=#{ordNo} and BZODR_NO=#{bzodrNo} and BRST_ECD = #{brstEcd}
and NODE_ID like CONCAT('%',#{nodeId},'%')
and EXTN_FLOWT_ID = #{extnFlowtId}
order by CRT_TM
